import { useState } from "react";
import emailjs from "emailjs-com";
import React from "react";

const initialState = {
  name: "",
  email: "",
  message: "",
};
export const Contact = (props) => {
  const [{ name, email, message }, setState] = useState(initialState);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setState((prevState) => ({ ...prevState, [name]: value }));
  };
  const clearState = () => setState({ ...initialState });


  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(name, email, message);

    // {/* replace below with your own Service ID, Template ID and Public Key from your EmailJS account */ }

    emailjs
      .sendForm("YOUR_SERVICE_ID", "YOUR_TEMPLATE_ID", e.target, "YOUR_PUBLIC_KEY")
      .then(
        (result) => {
          console.log(result.text);
          clearState();
        },
        (error) => {
          console.log(error.text);
        }
      );
  };
  return (
    <div>
      <div id="contact">
  <div className="container-fluid">
    <div className="row section-title text-center">
      <div className="col-md-12">
        <h2>Get In Touch</h2>
        <p>
          At K & H Associates, we provide expert chartered accounting services.
          Whether you're a business or an individual, we’re here to ensure your
          financial affairs are managed efficiently and effectively.
        </p>
      </div>
    </div>

    <div className="row ">
      <div className="col-lg-8 col-md-6 section-title">
        <form name="sentMessage" onSubmit={handleSubmit}>
               <p>Please fill out the form below to send us an email and we will getback to you as soon as possible. </p>

              <div className="form-group">
                <input
                  type="text"
                  id="name"
                  name="name"
                  className="form-control"
                  placeholder="Name"
                  required
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <input
                  type="email"
                  id="email"
                  name="email"
                  className="form-control"
                  placeholder="Email"
                  required
                  onChange={handleChange}
                />
              </div>

              <div className="form-group">
                <textarea
                  name="message"
                  id="message"
                  className="form-control"
                  rows="4"
                  placeholder="Message"
                  required
                  onChange={handleChange}
                ></textarea>
              </div>
              <button type="submit" className="btn btn-custom btn-lg">
                Send Message
              </button>
        </form>
        </div>

      <div className="col-md-4">
        <div className="contact-info">
          <h3>Contact Info</h3>
          <p>
            <span>
              <i className="fa fa-map-marker"></i> Address:
            </span>{" "}
            {props.data ? props.data.address : "Loading..."}
          </p>
          <p>
            <span>
              <i className="fa fa-phone"></i> Phone:
            </span>{" "}
            {props.data ? props.data.phone : "Loading..."}
          </p>
          <p>
            <span>
              <i className="fa fa-envelope-o"></i> Email:
            </span>{" "}
            {props.data ? props.data.email : "Loading..."}
          </p>
          </div>
          </div>
    </div>

  </div>
</div>


      <div id="footer">
        <div className="container text-center">
          <p>
            &copy; 2023 Issaaf Kattan React Land Page Template. Design by{" "}
            <a href="http://www.templatewire.com" rel="nofollow">
              TemplateWire
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};
