import React from "react";

export const Features = (props) => {
  return (
    <div id="features" className="text-center">
      <div className="container">
        <div className="col-md-10 col-md-offset-1 section-title">
          <h2>Home</h2>
          <p>Welcome to K & H Associates, the unrivaled choice for professional accounting services. We excel in tax planning, business consulting, 
            audit & assurance, and financial statement preparation. Let's unite to conquer your financial goals. </p>
            <h3>Why to choose us ?</h3>
            <p>We confidently guarantee the accuracy of your financial records, ensure compliance with tax obligations, and provide expert strategic 
              advice to minimize tax liabilities and optimize your financial performance.</p>

        </div>

      </div>
    </div>
  );
};
