export const Services = (props) => {
  return (
    <div id="services" className="text-center">
      <div className="container-fluid">
        <div className="section-title">
          <h2>Our Services</h2>
          <p>
            At K & H Associates, we believe that every business deserves
            top-notch accounting services. Let us help you take your business to
            the level with our expert guidance.
          </p>
        </div>
        <section className="py-20 bg-white">
  <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
    <h2 className="text-center text-3xl font-bold text-gray-900 mb-12">Our Gallery</h2>
    <div className="flex flex-wrap justify-center gap-6">
      {[1, 2, 3, 4].map((item, index) => (
        <div
          key={index}
          className="w-full sm:w-1/2 lg:w-1/4 p-2 border border-gray-200 rounded-xl hover:shadow-lg transition duration-300"
        >
          <img
            src="img/about.jpg"
            alt={`About us ${index + 1}`}
            className="rounded-lg w-full h-60 object-cover"
          />
        </div>
      ))}
    </div>
  </div>
</section>

      </div>
    </div>
  );
};
